firestore.rules:
rules_version = '2';
service cloud.firestore {
  match /databases/{database}/documents {
    // قواعد عامة للقراءة للجميع
    match /{document=**} {
      allow read: if true;
    }

    // الحسابات: القراءة للجميع، الكتابة للمسجلين
    match /accounts/{accountId} {
      allow read: if true;
      allow create: if request.auth != null;
      allow update, delete: if request.auth != null && resource.data.sellerId == request.auth.uid;
    }

    // المستخدمون: الجميع يمكنهم الإنشاء والتحديث الذاتي
    match /users/{userId} {
      allow read: if true;
      allow create: if true;
      allow update: if request.auth != null && request.auth.uid == userId;
    }

    // المحادثات: للمسجلين فقط
    match /chats/{chatId} {
      allow read, write: if request.auth != null;
    }

    // الرسائل: للمسجلين فقط
    match /chats/{chatId}/messages/{messageId} {
      allow read, write: if request.auth != null;
    }

    // الدعم الفني: الإنشاء للجميع، القراءة للمسجلين
    match /support/{ticketId} {
      allow read: if request.auth != null;
      allow create: if true;
    }
  }
}

storage.rules:
rules_version = '2';
service firebase.storage {
  match /b/{bucket}/o {
    match /{allPaths=**} {
      allow read: if true;
      allow write: if request.auth != null;
    }
  }
}
